mapper.select:
SELECT
`id`,
`corp_id`,
`emple_code_id`,
`user_id`,
`external_userid`,
`add_code_time` as `time`,
`type`
FROM
we_emple_code_analyse
WHERE corp_id = #{corpId}

    AND emple_code_id = #{state}


    AND user_id = #{userId}


    AND `add_code_time` >= #{beginTime}


    AND `add_code_time` <= #{endTime}

SELECT
SUM(IF( `type` = 1, 1, 0 )) addCount,
SUM(IF( `type` = 0, 1, 0 )) loseCount,
`add_code_time` AS `time`
FROM
we_emple_code_analyse
WHERE corp_id = #{corpId}

    AND emple_code_id = #{state}


    AND user_id = #{userId}


    AND user_id IN(

        #{item}

    )


    AND `add_code_time` >= #{beginTime}


    AND `add_code_time` <= #{endTime}

GROUP BY `add_code_time`

SELECT emple_code_id, user_id, SUM(CASE WHEN type = '${@com.easyink.common.enums.WeEmpleCodeAnalyseTypeEnum@ADD.type}' THEN 1 ELSE 0 END) AS newCustomerCnt, SUM(CASE WHEN type = '${@com.easyink.common.enums.WeEmpleCodeAnalyseTypeEnum@DEL.type}' THEN 1 ELSE 0 END) AS lossCustomerCnt, `add_code_time` AS `time` FROM we_emple_code_analyse WHERE corp_id = #{corpId}
AND emple_code_id IN
#{empleCodeId}
AND `add_code_time` = #{date} GROUP BY emple_code_id, user_id

SELECT emple_code_id,
user_id,
SUM(CASE WHEN type = '${@com.easyink.common.enums.WeEmpleCodeAnalyseTypeEnum@ADD.type}' THEN 1 ELSE 0 END) AS accumulateCustomerCnt
FROM we_emple_code_analyse
WHERE corp_id = #{corpId}

AND emple_code_id IN

        #{empleCodeId}


AND `add_code_time` <= #{date}
GROUP BY emple_code_id, user_id

SELECT weca.emple_code_id, weca.user_id, SUM(CASE WHEN wfcr.status IN ('${@com.easyink.common.enums.CustomerStatusEnum@NORMAL.code}', '${@com.easyink.common.enums.CustomerStatusEnum@TO_BE_TRANSFERRED.code}', '${@com.easyink.common.enums.CustomerStatusEnum@TRANSFERRING.code}' ) THEN 1 ELSE 0 END) AS retainCustomerCnt FROM we_emple_code_analyse weca LEFT JOIN we_flower_customer_rel wfcr ON weca.corp_id = wfcr.corp_id AND weca.user_id = wfcr.user_id AND weca.external_userid = wfcr.external_userid AND wfcr.create_time <= DATE_FORMAT(weca.`add_code_time`, '%Y-%m-%d 23:59:59') AND wfcr.delete_time <= wfcr.create_time WHERE weca.corp_id = #{corpId}
AND weca.emple_code_id IN
#{empleCodeId}
AND type = '${@com.easyink.common.enums.WeEmpleCodeAnalyseTypeEnum@ADD.type}' AND weca.`add_code_time` <= #{date} GROUP BY weca.emple_code_id, weca.user_id

SELECT SUM(CASE WHEN weca.`type` = '${@com.easyink.common.enums.WeEmpleCodeAnalyseTypeEnum@ADD.type}' THEN 1 ELSE 0 END) AS accumulateCustomerCnt, SUM(CASE WHEN weca.`type` = '${@com.easyink.common.enums.WeEmpleCodeAnalyseTypeEnum@ADD.type}' and `add_code_time` = #{time} THEN 1 ELSE 0 END) AS newCustomerCnt, SUM(CASE WHEN weca.`type` = '${@com.easyink.common.enums.WeEmpleCodeAnalyseTypeEnum@DEL.type}' and `add_code_time` = #{time} THEN 1 ELSE 0 END) AS lossCustomerCnt FROM we_emple_code_analyse weca LEFT JOIN we_flower_customer_rel wfcr ON weca.corp_id = wfcr.corp_id AND weca.user_id = wfcr.user_id AND weca.external_userid = wfcr.external_userid AND wfcr.create_time <= DATE_FORMAT(#{time}, '%Y-%m-%d 23:59:59') WHERE weca.corp_id = #{corpId}
AND weca.channel_id IN
#{channelId}

SELECT
SUM(CASE WHEN weca.`type` = '${@com.easyink.common.enums.WeEmpleCodeAnalyseTypeEnum@ADD.type}' THEN 1 ELSE 0 END) AS newCustomerCnt,
wecc.`name`
FROM we_emple_code_analyse weca INNER JOIN we_emple_code_channel wecc ON wecc.id = weca.channel_id
WHERE weca.corp_id = #{corpId}

    AND weca.channel_id IN

        #{channelId}



    AND `add_code_time` >= #{beginTime}


    AND `add_code_time` <= #{endTime}

GROUP BY wecc.`name`

SELECT
    `add_code_time` as `time`,
    SUM(CASE WHEN `type` = '${@com.easyink.common.enums.WeEmpleCodeAnalyseTypeEnum@ADD.type}' THEN 1 ELSE 0 END) AS newCustomerCnt,
    SUM(CASE WHEN `type` = '${@com.easyink.common.enums.WeEmpleCodeAnalyseTypeEnum@DEL.type}' THEN 1 ELSE 0 END) AS lossCustomerCnt
FROM we_emple_code_analyse
WHERE corp_id = #{corpId}

    AND channel_id IN

        #{channelId}



    AND `add_code_time` >= #{beginTime}


    AND `add_code_time` <= #{endTime}

GROUP BY `add_code_time`

SELECT
    wc.`name` AS externalUserName, wc.`avatar` AS externalUserHeadImage, wc.external_userid, wu.user_name, wu.user_id, wd.`name` AS departmentName, DATE_FORMAT(weca.add_time,
'%Y-%m-%d %H:%i') AS addTime, weca.channel_id AS state
FROM we_emple_code_analyse weca
INNER JOIN we_customer wc ON weca.external_userid = wc.external_userid
INNER JOIN we_user wu ON weca.user_id = wu.user_id
INNER JOIN we_department wd ON wu.main_department = wd.id AND wd.corp_id = weca.corp_id
WHERE weca.corp_id = #{corpId} AND weca.`type` = '${@com.easyink.common.enums.WeEmpleCodeAnalyseTypeEnum@ADD.type}'

    AND wc.name LIKE CONCAT("%", #{name}, "%")



        AND wu.user_id in

            #{item}



        AND wu.user_id=#{userIds}



    AND weca.channel_id IN

        #{channelId}



    AND weca.channel_id = #{channelId}


    AND weca.add_time >= #{beginTime}


    AND weca.add_time <= #{endTime}

ORDER BY addTime DESC

SELECT SUM(CASE WHEN weca.`type` = '${@com.easyink.common.enums.WeEmpleCodeAnalyseTypeEnum@ADD.type}' THEN 1 ELSE 0 END) AS newCustomerCnt, SUM(CASE WHEN weca.`type` = '${@com.easyink.common.enums.WeEmpleCodeAnalyseTypeEnum@DEL.type}' THEN 1 ELSE 0 END) AS lossCustomerCnt, weca.channel_id, wecc.`name`, wecc.emple_code_id FROM we_emple_code_analyse weca INNER JOIN we_emple_code_channel wecc ON wecc.id = weca.channel_id LEFT JOIN we_flower_customer_rel wfcr ON weca.corp_id = wfcr.corp_id AND weca.user_id = wfcr.user_id AND weca.external_userid = wfcr.external_userid WHERE weca.corp_id = #{corpId}
AND weca.user_id IN
#{item}
AND weca.user_id=#{userIds}
AND weca.channel_id IN
#{channelId}
AND weca.channel_id = #{channelId}
AND weca.`add_code_time` >= DATE_FORMAT(#{beginTime}, '%Y-%m-%d')
AND weca.`add_code_time` <= DATE_FORMAT(#{endTime}, '%Y-%m-%d')
GROUP BY weca.channel_id

SELECT SUM(CASE WHEN weca.`type` = '${@com.easyink.common.enums.WeEmpleCodeAnalyseTypeEnum@ADD.type}' THEN 1 ELSE 0 END) AS newCustomerCnt,
SUM(CASE WHEN weca.`type` = '${@com.easyink.common.enums.WeEmpleCodeAnalyseTypeEnum@DEL.type}' THEN 1 ELSE 0 END) AS lossCustomerCnt,
weca.`add_code_time` AS `date`
FROM we_emple_code_analyse weca INNER JOIN we_emple_code_channel wecc ON wecc.id = weca.channel_id LEFT JOIN we_flower_customer_rel wfcr ON weca.corp_id = wfcr.corp_id AND weca.user_id = wfcr.user_id
AND weca.external_userid = wfcr.external_userid
WHERE weca.corp_id = #{corpId}


        AND weca.user_id IN

            #{item}



        AND weca.user_id=#{userIds}



    AND weca.channel_id IN

        #{channelId}



    AND weca.channel_id = #{channelId}


    AND weca.`add_code_time` >= DATE_FORMAT(#{beginTime}, '%Y-%m-%d')


    AND weca.`add_code_time` <= DATE_FORMAT(#{endTime}, '%Y-%m-%d')

GROUP BY `date`

SELECT COUNT(0) AS currentNewCustomerCnt, state FROM we_flower_customer_rel WHERE corp_id = #{corpId}
AND state IN
#{state}
AND create_time >= DATE_FORMAT(#{time}, '%Y-%m-%d 00:00:00') AND create_time <= DATE_FORMAT(#{time}, '%Y-%m-%d 23:59:59')
AND status IN ('${@com.easyink.common.enums.CustomerStatusEnum@NORMAL.code}', '${@com.easyink.common.enums.CustomerStatusEnum@TO_BE_TRANSFERRED.code}', '${@com.easyink.common.enums.CustomerStatusEnum@TRANSFERRING.code}' )
