mapper.delete:
delete
from we_emple_code
where corp_id = #{corpId}
  and id = #{id}

mapper.select:
SELECT wec.id, wec.scenario, wec.qr_code, wec.app_link, wec.create_by, wec.create_time, wec.source, wec.state
FROM we_emple_code wec
INNER JOIN (SELECT wecus.emple_code_id, wecus.business_id, wecus.business_id_type, wecus.business_name, wu.mobile
FROM we_emple_code_use_scop wecus LEFT JOIN we_user wu ON wu.user_id = wecus.business_id AND wu.corp_id = #{corpId}
WHERE wecus.del_flag = ${@com.easyink.common.constant.Constants@NORMAL_CODE}) as wecusn on wec.id = wecusn.emple_code_id
WHERE wec.corp_id = #{corpId} AND wec.del_flag = ${@com.easyink.common.constant.Constants@NORMAL_CODE}

    AND wec.source = #{source}


    and wec.scenario like concat('%', #{scenario}, '%')


    AND wec.create_by like concat('%', #{createBy}, '%')


    AND wec.create_time >= #{beginTime}


    AND wec.create_time <= #{endTime}


    AND wecusn.business_name like concat('%', #{useUserName}, '%')

GROUP BY wec.id
order by wec.create_time DESC

select wecode.id as emple_code_id, wecode.welcome_msg , wm.category_id, wm.material_url, wm.content, wm.material_name, wm.digest, wm.cover_url from we_emple_code wecode left join we_emple_code_use_scop wecus on wecode.id = wecus.emple_code_id left join we_material wm on wm .id = wecode.media_id where wecode.corp_id = #{corpId} and wecode.del_flag = ${@com.easyink.common.constant.WeConstans@WE_CUSTOMER_MSG_RESULT_NO_DEFALE}
and wecode.scenario = #{scenario}
and wecus.business_id = #{userId}

SELECT id, scenario FROM we_emple_code WHERE corp_id = #{corpId}
AND scenario LIKE CONCAT('%', #{scenario}, '%')
AND del_flag = '${@com.easyink.common.constant.Constants@NORMAL_CODE}' ORDER BY create_time DESC

SELECT wec.id, wec.scenario, wec.qr_code, GROUP_CONCAT(wecusn.business_name), wec.create_by, wec.create_time, IFNULL(wd.name,'') AS mainDepartmentName FROM we_emple_code wec LEFT JOIN (SELECT wecus.emple_code_id, wecus.business_id, wecus.business_id_type, wecus.business_name, wu.mobile FROM we_emple_code_use_scop wecus LEFT JOIN we_user wu ON wu.user_id = wecus.business_id AND wu.corp_id = #{corpId} WHERE wecus.del_flag = 0) as wecusn on wec.id = wecusn.emple_code_id LEFT JOIN we_user wu ON wec.create_by = wu.user_name AND wu.corp_id = wec.corp_id LEFT JOIN we_department wd ON wd.id = wu.main_department AND wd.corp_id = wu.corp_id WHERE wec.corp_id = #{corpId} AND wec.del_flag = 0 ${params.dataScope}
and wec.scenario like concat('%', #{scenario}, '%')
AND wec.create_by like concat('%', #{createBy}, '%')
AND wec.create_time >= #{beginTime}
AND wec.create_time <= #{endTime}
AND wecusn.business_name like concat('%', #{useUserName}, '%')
AND wec.source = #{source}
AND wec.qr_code like concat('%', #{qrCode}, '%')
GROUP BY wec.id order by wec.create_time DESC

SELECT scenario, create_by
FROM we_emple_code
WHERE corp_id = #{corpId} AND link_id = #{linkId}
LIMIT 1;
