SELECT
COUNT(id)
FROM
we_group_member wgm
WHERE
wgm.type = #{type}
AND wgm.join_scene = #{joinScene}
AND wgm.chat_id IN

        #{item}

SELECT
    SUM(if(`type` = ${@com.easyink.common.constant.WeConstans@WE_GROUP_MEMBER_TYPE_STAFF},1,0)) staffCount,
    SUM(if(`type` = ${@com.easyink.common.constant.WeConstans@WE_GROUP_MEMBER_TYPE_CUSTOMER},1,0)) customerCount
FROM
    we_group_member
WHERE
    `corp_id` = #{corpId}
    AND `chat_id` = #{chatId}

        AND `name` LIKE concat('%', #{memberName}, '%')


        AND `join_time` >= #{startTime}


        AND `join_time` <= #{endTime}


        AND `join_scene` = #{joinScene}

SELECT wgm.id, wgm.user_id, wgm.chat_id, wgm.corp_id, wgm.name AS memberName, wgm.union_id, wgm.join_time, wgm.join_scene, wgm.type AS joinType, wgm.invitor, (SELECT wu.user_name FROM we_user wu WHERE wu.corp_id = wgm.corp_id AND wu.user_id = wgm.invitor) invitorName FROM we_group_member wgm LEFT JOIN we_user wu ON wu.user_id = wgm.invitor AND wu.`corp_id` = wgm.`corp_id` WHERE wgm.`corp_id` = #{corpId} AND wgm.`chat_id` = #{chatId}
AND wgm.`name` LIKE concat('%', #{memberName}, '%')
AND wgm.`join_time` >= #{startTime}
AND wgm.`join_time` <= #{endTime}
AND wgm.`join_scene` = #{joinScene}

SELECT wgm.chat_id, wgm.join_time, wgm.type, wgm.join_scene, IFNULL(wu.head_image_url,wc.avatar) as member_avatar, IFNULL(wu.user_name,wc.name) as member_name FROM we_group_member wgm left join we_user wu on wu.user_id = wgm.user_id and wgm.type = ${@com.easyink.common.constant.GroupConstants@ID_TYPE_USER} and wu.corp_id = wgm.corp_id left join we_customer wc on wc.external_userid = wgm.user_id and wgm.type = ${@com.easyink.common.constant.GroupConstants@ID_TYPE_CUSTOMER} and wc.corp_id = wgm.corp_id
and wgm.chat_id IN
#{chatId}
