mapper.select:
SELECT wt.tag_id, wt.`name` AS tagName, wtg.group_id AS tagGroupId, wtg.group_name AS groupTagName FROM we_tag wt INNER JOIN we_tag_group wtg ON wt.group_id = wtg.group_id AND wt.`status` = wtg.`status` WHERE wtg.corp_id = #{dto.corpId} AND wtg.`status` = ${@com.easyink.common.constant.Constants@NORMAL_CODE}
AND wtg.group_id IN
#{tagGroupId}
AND wt.tag_id IN
#{tagId}

SELECT wfctr.`tag_id`, wt.`name`, wt.create_time FROM we_flower_customer_tag_rel wfctr INNER JOIN we_tag wt ON wfctr.tag_id = wt.tag_id INNER JOIN we_tag_group wtg ON wt.group_id = wtg.group_id AND wt.`status` = wtg.`status` WHERE wtg.corp_id = #{corpId} AND wt.`status` = ${@com.easyink.common.constant.Constants@NORMAL_CODE}
AND wfctr.flower_customer_rel_id IN
#{flowerCustomerRelId}
AND wt.group_id IN
#{tagGroupId}
GROUP BY external_userid, tag_id

SELECT wfctr.tag_id, wfctr.external_userid AS externalUserId FROM we_flower_customer_tag_rel wfctr INNER JOIN we_tag wt ON wfctr.tag_id = wt.tag_id INNER JOIN we_tag_group wtg ON wt.group_id = wtg.group_id AND wt.`status` = wtg.`status` WHERE wtg.corp_id = #{corpId} AND wt.`status` = ${@com.easyink.common.constant.Constants@NORMAL_CODE}
AND wfctr.flower_customer_rel_id IN
#{flowerCustomerRelId}
AND wt.group_id IN
#{tagGroupId}
GROUP BY external_userid, tag_id

mapper.update:
update we_tag
group_id = #{groupId}, name = #{name}, create_time = #{createTime},
where id = #{id}

mapper.insert:
INSERT INTO we_tag (tag_id,group_id,corp_id,`name`,create_time)
VALUES

    (
        #{item.tagId},#{item.groupId},#{item.corpId},#{item.name}
        ,#{item.createTime}
    )

ON DUPLICATE KEY UPDATE
`name` = values(`name`)
