mapper.select:
SELECT ucms.user_id, wu.`user_name`, wd.`name` departmentName, wu.`head_image_url` userHeadImage, IFNULL((SELECT COUNT(wucms.external_userid) FROM we_user_customer_message_statistics wucms WHERE wucms.corp_id = ucms.corp_id AND wucms.user_id = ucms.user_id
), 0) AS chatTotal, IFNULL(SUM(ucms.user_send_message_cnt), 0) AS sendContactCnt, IF(IFNULL((SELECT COUNT(wucms.external_userid) FROM we_user_customer_message_statistics wucms WHERE wucms.corp_id = ucms.corp_id AND wucms.user_id = ucms.user_id ), 0) = 0, 0, CAST(SUM(ucms.user_send_message_cnt) *1./ (SELECT COUNT(wucms.external_userid) FROM we_user_customer_message_statistics wucms WHERE wucms.corp_id = ucms.corp_id AND wucms.user_id = ucms.user_id ) as decimal(8,2))) AS averageChatTotal, IFNULL((SELECT IF(IFNULL(COUNT(wucms.external_userid), 0)=0,0,CAST(IFNULL(SUM(wucms.first_reply_time_interval_alter_receive), 0) *1./ COUNT(wucms.external_userid) as decimal(8,2))) FROM we_user_customer_message_statistics wucms WHERE wucms.corp_id = ucms.corp_id AND wucms.user_id = ucms.user_id AND wucms.user_active_dialogue = '${@com.easyink.common.enums.ContactSpeakEnum@CUSTOMER.code}'
), 0) AS averageFirstReplyDuration, IFNULL((SELECT COUNT(wucms.id) FROM we_user_customer_message_statistics wucms WHERE wucms.corp_id = ucms.corp_id AND wucms.user_id = ucms.user_id AND wucms.user_active_dialogue = '${@com.easyink.common.enums.ContactSpeakEnum@CUSTOMER.code}'
),0) AS customerActiveStartContactCnt, IFNULL((SELECT COUNT(wucms.id) FROM we_user_customer_message_statistics wucms WHERE wucms.corp_id = ucms.corp_id AND wucms.user_id = ucms.user_id AND wucms.user_send_message_cnt != '${@com.easyink.common.constant.WeConstans@ZERO}' AND wucms.user_active_dialogue = '${@com.easyink.common.enums.ContactSpeakEnum@CUSTOMER.code}'
), 0) AS userReplyContactCnt,
IF(IFNULL((SELECT COUNT(wucms.id) FROM we_user_customer_message_statistics wucms WHERE wucms.corp_id = ucms.corp_id AND wucms.user_id = ucms.user_id AND wucms.user_active_dialogue = '${@com.easyink.common.enums.ContactSpeakEnum@CUSTOMER.code}'
),0) = 0, 0, CAST(IFNULL((SELECT COUNT(wucms.id) FROM we_user_customer_message_statistics wucms WHERE wucms.corp_id = ucms.corp_id AND wucms.user_id = ucms.user_id AND wucms.user_send_message_cnt != '${@com.easyink.common.constant.WeConstans@ZERO}' AND wucms.user_active_dialogue = '${@com.easyink.common.enums.ContactSpeakEnum@CUSTOMER.code}'
),0) * 1./ (SELECT COUNT(wucms.id) FROM we_user_customer_message_statistics wucms WHERE wucms.corp_id = ucms.corp_id AND wucms.user_id = ucms.user_id AND wucms.user_active_dialogue = '${@com.easyink.common.enums.ContactSpeakEnum@CUSTOMER.code}'
) as decimal(8,2))) AS replyRate,
IF(IFNULL((SELECT COUNT(wucms.external_userid) FROM we_user_customer_message_statistics wucms WHERE wucms.corp_id = ucms.corp_id AND wucms.user_id = ucms.user_id ), 0) = 0, 0, CAST(IFNULL(SUM(three_rounds_dialogue_flag),0) *1./ (SELECT COUNT(wucms.external_userid) FROM we_user_customer_message_statistics wucms WHERE wucms.corp_id = ucms.corp_id AND wucms.user_id = ucms.user_id ) as decimal(8,2))) AS effectiveCommunicationRate,
IFNULL(SUM(ucms.three_rounds_dialogue_flag),0) AS effectiveCommunicationCustomerCnt, IFNULL(SUM(wfcf.score), 0) AS score,
IF(IFNULL(COUNT(wfcf.customer_id), 0)=0, 0, CAST(IFNULL(SUM(wfcf.score), 0) *1./COUNT(wfcf.customer_id) AS DECIMAL(8,2))) AS customerPositiveCommentsRate,
IFNULL(COUNT(wfcf.customer_id), 0) AS num FROM we_user_customer_message_statistics ucms LEFT JOIN we_form_customer_feedback wfcf ON wfcf.corp_id = ucms.corp_id AND wfcf.user_id = ucms.user_id AND wfcf.create_time BETWEEN date_format(#{beginTime},'%Y-%m-%d 00:00:00' ) AND date_format(#{endTime},'%Y-%m-%d 23:59:59' ) LEFT JOIN we_user wu ON wu.user_id = ucms.user_id AND wu.corp_id = ucms.corp_id LEFT JOIN we_department wd ON wd.id = wu.main_department AND wd.corp_id = wu.corp_id WHERE ucms.corp_id = #{corpId} ${params.dataScope} AND wu.is_activate = ${@com.easyink.common.constant.WeConstans@WE_USER_IS_ACTIVATE} AND wu.`user_name` IS NOT NULL
AND ucms.send_time >= #{beginTime}
AND ucms.send_time <= #{endTime}
AND ucms.user_id IN
#{userId}
AND ucms.user_id NOT IN
#{userId}
GROUP BY ucms.user_id
ORDER BY chatTotal ${chatTotalSort}
ORDER BY sendContactCnt ${sendContactCntSort}
ORDER BY averageChatTotal ${averageChatTotalSort}
ORDER BY averageFirstReplyDuration ${averageFirstReplyDurationSort}
ORDER BY replyRate ${replyRateSort}
ORDER BY effectiveCommunicationCustomerCnt ${effectiveCommunicationCustomerCntSort}
ORDER BY effectiveCommunicationRate ${effectiveCommunicationRateSort}
ORDER BY customerPositiveCommentsRate ${customerPositiveCommentsRateSort}

