select wu.is_activate, wu.head_image_url, wu.user_name, wu.alias, wu.user_id, wu.gender, wu.mobile, wu.email, wu.department, wu.position, wu.join_time, wu.enable, wu.id_card, wu.qq_account, wu.address, wu.birthday, wu.main_department, wu.is_allocate, sr.role_id, sr.role_name, wu.remark, wd.name as mainDepartmentName from we_user wu left join we_user_role r on r.user_id = wu.user_id and r.corp_id = wu.corp_id left join sys_role sr on sr.role_id = r.role_id and sr.corp_id = wu.corp_id left join we_department wd on wu.main_department = wd.id and wd.corp_id = wu.corp_id WHERE wu.corp_id = #{corpId} ${params.dataScope}
AND sr.role_id = #{roleId}
AND wu.is_activate = #{isActivate} AND wu.is_activate != ${@com.easyink.common.constant.WeConstans@WE_USER_IS_LEAVE}
AND position(#{userName} IN wu.user_name)
AND wu.is_allocate = #{isAllocate}
AND CONCAT(',',wu.main_department,',') REGEXP CONCAT(',(',REPLACE(#{departments},',','|'),'),')

select wu.is_activate, wu.head_image_url, wu.user_name, wu.alias, wu.user_id, wu.gender, wu.mobile, wu.email, wu.department, wu.position, wu.join_time, wu.enable, wu.id_card, wu.qq_account, wu.address, wu.birthday, wu.main_department, wu.is_allocate, sr.role_id, sr.role_name, wu.remark, wd.name as mainDepartmentName from we_user wu left join we_user_role r on r.user_id = wu.user_id and r.corp_id = wu.corp_id left join sys_role sr on sr.role_id = r.role_id and sr.corp_id = wu.corp_id left join we_department wd on wu.main_department = wd.id and wd.corp_id = wu.corp_id WHERE wu.corp_id = #{corpId} AND wu.is_activate != ${@com.easyink.common.constant.WeConstans@WE_USER_IS_LEAVE}
AND wu.user_id in
#{item}


FROM we_user wu
WHERE
wu.corp_id = #{corpId}
AND wu.is_activate != ${@com.easyink.common.constant.WeConstans@WE_USER_IS_LEAVE}
${params.dataScope}
and wu.is_activate = #{isActivate}
and wu.head_image_url = #{avatarMediaid}
and wu.user_name like concat('%', #{name}, '%')
and wu.alias = #{alias}
and wu.user_id = #{userId}
and wu.gender = #{gender}
and wu.mobile = #{mobile}
and wu.email = #{email}
and wu.wx_account = #{wxAccount}

    and CONCAT(',',wu.main_department,',') REGEXP CONCAT(',(',REPLACE(#{departmentStr},',','|'),'),')

and wu.position = #{position}
and wu.is_leader_in_dept = #{isLeaderInDept}

    and wu.join_time BETWEEN #{beginTime} AND #{endTime}

and wu.id_card = #{idCard}
and wu.qq_account = #{qqAccount}
and wu.telephone = #{telephone}
and wu.address = #{address}
and wu.birthday = #{birthday}
and wu.main_department = #{mainDepartment}
and wu.user_id > #{lastId}

    AND wu.department = ${@com.easyink.common.constant.WeConstans@OTHER_USER_DEPARTMENT}

SELECT wu.user_id FROM we_user wu
LEFT JOIN we_department wd ON wd.id = wu.department
WHERE
wu.corp_id = #{corpId}
and wu.is_activate = ${@com.easyink.common.constant.WeConstans@WE_USER_IS_ACTIVATE}

    AND wu.main_department in

        #{item}


GROUP BY wu.user_id


FROM we_user wu
WHERE
wu.corp_id = #{corpId}
and wu.is_activate != ${@com.easyink.common.constant.WeConstans@WE_USER_IS_LEAVE}

    AND wu.main_department IN

        #{item}

SELECT wu.user_id,
wu.user_name,
wu.head_image_url,
wu.main_department,
wu.alias,
wu.dimission_time,
wd.name as mainDepartmentName,
wu.is_allocate,
(SELECT count(id) FROM we_flower_customer_rel WHERE corp_id = #{corpId} AND user_id = wu.user_id
and status = ${@com.easyink.common.enums.CustomerStatusEnum@TO_BE_TRANSFERRED.code}
) as allocateCustomerNum,
(SELECT count(chat_id) FROM we_group WHERE corp_id =#{corpId} AND owner = wu.user_id
and status = ${@com.easyink.common.constant.GroupConstants@OWNER_LEAVE}
) as allocateGroupNum
FROM we_user wu
LEFT JOIN we_department wd ON wu.corp_id = wd.corp_id AND wd.id = wu.main_department

    wu.corp_id = #{corpId}
    ${params.dataScope}
    AND wu.is_activate in (${@com.easyink.common.enums.StaffActivateEnum@RETIRE.code},
    ${@com.easyink.common.enums.StaffActivateEnum@DELETE.code})
    AND wu.is_allocate = ${@com.easyink.common.constant.WeConstans@LEAVE_NO_ALLOCATE_STATE}

        AND position(#{userName} IN wu.user_name)


        AND wu.dimission_time >= #{beginTime}


        AND wu.dimission_time <= #{endTime}

SELECT
    wfcr.id
FROM
    we_customer wc
        INNER JOIN we_flower_customer_rel wfcr ON wfcr.external_userid = wc.external_userid
        INNER JOIN we_user wu ON wu.user_id = wfcr.user_id
        LEFT JOIN we_department wd ON wu.corp_id = wd.corp_id AND wd.id = wu.main_department
WHERE
    wc.corp_id = #{corpId}
    ${params.dataScope}
    AND wu.is_activate = ${@com.easyink.common.constant.WeConstans@WE_USER_IS_ACTIVATE}
    AND wfcr.`status` != ${@com.easyink.common.constant.Constants@DELETE_CODE}

    AND wu.user_id IN

        #{userId}



    AND wfcr.create_time >= #{beginTime}


    AND wfcr.create_time <= #{endTime}
