mapper.select:
SELECT COUNT(distinct external_user_id) clickNum
FROM we_radar_click_record
WHERE radar_id = #{radarId}

SELECT wr.id, wr.radar_title, wr.url, wr.type, wr.cover_url, wr.title, wr.content, wr.is_defined, wr.create_time, wr.enable_click_notice, wr.enable_behavior_record, wr.enable_customer_tag, wr.enable_update_notice, wr.create_by user_id, IFNULL(wu.user_name, wr.create_by) AS user_name, wu.main_department, wd.name, (SELECT COUNT(distinct external_user_id) FROM we_radar_click_record WHERE radar_id = wr.id) as clickNum from we_radar wr LEFT JOIN we_user wu ON wu.corp_id = wr.corp_id AND wu.user_id = wr.create_by LEFT JOIN we_department wd ON wr.corp_id = wd.corp_id AND wu.main_department = wd.id WHERE wr.corp_id = #{radar.corpId}
AND ( wr.radar_title like concat('%', #{radar.searchTitle}, '%') or wr.title like concat('%', #{radar.searchTitle}, '%') )
AND wu.user_id in
#{id}
AND wr.type = #{radar.type}
ORDER BY clickNum asc, wr.create_time desc
ORDER BY clickNum desc, wr.create_time desc
ORDER BY wr.create_time desc

SELECT url
FROM we_radar
WHERE id = #{id}
