mapper.select:
SELECT
SUM(CASE WHEN `time` = #{endDate} THEN accumulate_customer_cnt ELSE 0 END) AS accumulateCustomerCnt,
SUM(CASE WHEN `time` = #{endDate} THEN retain_customer_cnt ELSE 0 END) AS retainCustomerCnt,
SUM( new_customer_cnt ) AS newCustomerCnt,
SUM( loss_customer_cnt ) AS lossCustomerCnt
FROM
we_emple_code_statistic
WHERE corp_id = #{corpId}

    AND emple_code_id IN

         #{empleCodeId}



    AND user_id IN

        #{userId}



    AND `time` >= #{beginDate}

    AND `time` <= #{endDate}

SELECT user_id, COUNT(external_userid) currentNewCustomerCnt
FROM
we_flower_customer_rel
WHERE
corp_id = #{corpId}
AND `status` NOT IN ('${@com.easyink.common.enums.CustomerStatusEnum@DRAIN.code}', '${@com.easyink.common.enums.CustomerStatusEnum@DELETE.code}')

    AND external_userid IN

        #{externalUserId}



    AND user_id IN

        #{userId}


AND create_time >= #{beginTime}
AND create_time <= #{time}
GROUP BY user_id

SELECT wecs.user_id AS userId, wu.user_name AS userName, wd.name AS departmentName, wu.head_image_url AS userHeadImage, SUM( CASE WHEN `time` = #{endDate} THEN wecs.accumulate_customer_cnt ELSE 0 END ) AS accumulateCustomerCnt, SUM( CASE WHEN `time` = #{endDate} THEN wecs.retain_customer_cnt ELSE 0 END ) AS retainCustomerCnt, SUM( wecs.new_customer_cnt ) AS newCustomerCnt, SUM( wecs.loss_customer_cnt ) AS lossCustomerCnt FROM we_emple_code_statistic wecs LEFT JOIN we_user wu ON wecs.corp_id = wu.corp_id AND wecs.user_id = wu.user_id LEFT JOIN we_department wd ON wu.corp_id = wd.corp_id AND wu.main_department = wd.id WHERE wecs.corp_id = #{corpId}
AND wecs.emple_code_id IN
#{empleCodeId}
AND wecs.user_id IN
#{userId}
AND wecs.`time` >= #{beginDate}
AND wecs.`time` <= #{endDate}
GROUP BY wecs.user_id

SELECT wec.scenario AS empleName, wecs.emple_code_id AS empleCodeId, SUM( CASE WHEN `time` = #{endDate} THEN wecs.accumulate_customer_cnt ELSE 0 END ) AS accumulateCustomerCnt, SUM( CASE WHEN `time` = #{endDate} THEN wecs.retain_customer_cnt ELSE 0 END ) AS retainCustomerCnt, SUM( wecs.new_customer_cnt ) AS newCustomerCnt, SUM( wecs.loss_customer_cnt ) AS lossCustomerCnt FROM we_emple_code_statistic wecs LEFT JOIN we_emple_code wec ON wecs.corp_id = wec.corp_id AND wecs.emple_code_id = wec.id WHERE wecs.corp_id = #{corpId}
AND wecs.emple_code_id IN
#{empleCodeId}
AND wecs.user_id IN
#{userId}
AND wecs.`time` >= #{beginDate}
AND wecs.`time` <= #{endDate}
GROUP BY wecs.emple_code_id ORDER BY wec.create_time DESC

SELECT `state` AS empleCodeId, COUNT(external_userid) currentNewCustomerCnt
FROM
we_flower_customer_rel
WHERE
corp_id = #{corpId}
AND `status` NOT IN ('${@com.easyink.common.enums.CustomerStatusEnum@DRAIN.code}', '${@com.easyink.common.enums.CustomerStatusEnum@DELETE.code}')

    AND state IN

        #{empleCodeId}



    AND user_id IN

        #{userId}


AND create_time >= #{beginTime}
AND create_time <= #{time}
GROUP BY `state`

SELECT
`time`,
SUM( new_customer_cnt ) AS newCustomerCnt,
SUM( loss_customer_cnt ) AS lossCustomerCnt
FROM
we_emple_code_statistic
WHERE corp_id = #{corpId}

    AND emple_code_id IN

        #{empleCodeId}



    AND user_id IN

        #{userId}



    AND `time` >= #{beginDate}


    AND `time` <= #{endDate}

GROUP BY `time`

SELECT DATE_FORMAT(create_time, '%Y-%m-%d') AS `time`, COUNT(external_userid) currentNewCustomerCnt
FROM
we_flower_customer_rel
WHERE
corp_id = #{corpId}
AND `status` NOT IN ('${@com.easyink.common.enums.CustomerStatusEnum@DRAIN.code}', '${@com.easyink.common.enums.CustomerStatusEnum@DELETE.code}')

    AND external_userid IN

        #{externalUserId}



    AND user_id IN

        #{userId}


AND create_time <= #{time}
GROUP BY DATE_FORMAT(create_time, '%Y-%m-%d')

SELECT
`time`,
SUM(accumulate_customer_cnt)  AS accumulateCustomerCnt,
SUM(retain_customer_cnt) AS retainCustomerCnt
FROM
we_emple_code_statistic
WHERE corp_id = #{corpId}

    AND emple_code_id IN

        #{empleCodeId}



    AND user_id IN

        #{userId}



    AND `time` >= #{beginDate}


    AND `time` <= #{endDate}

GROUP BY `time`
