mapper.select:
SELECT
SUM(CASE WHEN wecs.`statistics_time` = #{endDate} THEN wecs.accumulate_customer_cnt ELSE 0 END) AS accumulateCustomerCnt,
SUM(CASE WHEN wecs.`statistics_time` = #{endDate} THEN wecs.retain_customer_cnt ELSE 0 END) AS retainCustomerCnt,
SUM( wecs.new_customer_cnt ) AS newCustomerCnt,
SUM( wecs.loss_customer_cnt ) AS lossCustomerCnt
FROM
we_emple_code_statistic wecs
LEFT JOIN we_user wu ON wu.user_id = wecs.user_id and wecs.corp_id = wu.corp_id
WHERE wecs.corp_id = #{corpId}

    AND wecs.emple_code_id IN

         #{empleCodeId}



    AND wecs.user_id IN

        #{userId}




    AND wu.position LIKE #{likePosition}


    AND wecs.`statistics_time` >= #{beginDate}

    AND wecs.`statistics_time` <= #{endDate}

SELECT wfcr.user_id, COUNT(wfcr.external_userid) currentNewCustomerCnt
FROM
we_flower_customer_rel wfcr
LEFT JOIN we_user wu ON wfcr.corp_id = wu.corp_id AND wfcr.user_id = wu.user_id
WHERE
wfcr.corp_id = #{corpId}
AND wfcr.`status` NOT IN ('${@com.easyink.common.enums.CustomerStatusEnum@DRAIN.code}', '${@com.easyink.common.enums.CustomerStatusEnum@DELETE.code}')

    AND wfcr.external_userid IN

        #{externalUserId}



    AND wfcr.user_id IN

        #{userId}




    AND wu.position LIKE #{likePosition}

AND wfcr.create_time >= #{beginTime}
AND wfcr.create_time <= #{time}
GROUP BY wfcr.user_id

SELECT wecs.user_id AS userId, wu.user_name AS userName, wu.position, wd.name AS departmentName, wu.head_image_url AS userHeadImage, SUM( CASE WHEN `statistics_time` = #{endDate} THEN wecs.accumulate_customer_cnt ELSE 0 END ) AS accumulateCustomerCnt, SUM( CASE WHEN `statistics_time` = #{endDate} THEN wecs.retain_customer_cnt ELSE 0 END ) AS retainCustomerCnt, SUM( wecs.new_customer_cnt ) AS newCustomerCnt, SUM( wecs.loss_customer_cnt ) AS lossCustomerCnt FROM we_emple_code_statistic wecs LEFT JOIN we_user wu ON wecs.corp_id = wu.corp_id AND wecs.user_id = wu.user_id LEFT JOIN we_department wd ON wu.corp_id = wd.corp_id AND wu.main_department = wd.id WHERE wecs.corp_id = #{corpId}
AND wecs.emple_code_id IN
#{empleCodeId}
AND wecs.user_id IN
#{userId}
AND wu.position LIKE #{likePosition}
AND wecs.`statistics_time` >= #{beginDate}
AND wecs.`statistics_time` <= #{endDate}
GROUP BY wecs.user_id

SELECT wec.scenario AS empleName, wecs.emple_code_id AS empleCodeId, SUM( CASE WHEN `statistics_time` = #{endDate} THEN wecs.accumulate_customer_cnt ELSE 0 END ) AS accumulateCustomerCnt, SUM( CASE WHEN `statistics_time` = #{endDate} THEN wecs.retain_customer_cnt ELSE 0 END ) AS retainCustomerCnt, SUM( wecs.new_customer_cnt ) AS newCustomerCnt, SUM( wecs.loss_customer_cnt ) AS lossCustomerCnt FROM we_emple_code_statistic wecs LEFT JOIN we_emple_code wec ON wecs.corp_id = wec.corp_id AND wecs.emple_code_id = wec.id LEFT JOIN we_user wu ON wecs.corp_id = wu.corp_id AND wecs.user_id = wu.user_id WHERE wecs.corp_id = #{corpId}
AND wecs.emple_code_id IN
#{empleCodeId}
AND wecs.user_id IN
#{userId}
AND wecs.`statistics_time` >= #{beginDate}
AND wecs.`statistics_time` <= #{endDate}
AND wu.position LIKE #{likePosition}
GROUP BY wecs.emple_code_id ORDER BY wec.create_time DESC

SELECT wfcr.`state` AS empleCodeId, COUNT(wfcr.external_userid) currentNewCustomerCnt
FROM
we_flower_customer_rel wfcr
left join we_user wu on wfcr.corp_id = wu.corp_id and wfcr.user_id = wu.user_id
WHERE
wfcr.corp_id = #{corpId}
AND wfcr.`status` NOT IN ('${@com.easyink.common.enums.CustomerStatusEnum@DRAIN.code}', '${@com.easyink.common.enums.CustomerStatusEnum@DELETE.code}')

    AND wfcr.state IN

        #{empleCodeId}



    AND wfcr.user_id IN

        #{userId}




    AND wu.position LIKE #{likePosition}

AND wfcr.create_time >= #{beginTime}
AND wfcr.create_time <= #{time}
GROUP BY wfcr.`state`

SELECT
wecs.`statistics_time` as `time`,
SUM( wecs.new_customer_cnt ) AS newCustomerCnt,
SUM( wecs.loss_customer_cnt ) AS lossCustomerCnt
FROM
we_emple_code_statistic wecs
left JOIN we_user wu ON wecs.corp_id = wu.corp_id AND wecs.user_id = wu.user_id
WHERE wecs.corp_id = #{corpId}

    AND wecs.emple_code_id IN

        #{empleCodeId}



    AND wecs.user_id IN

        #{userId}




    AND wu.position LIKE #{likePosition}


    AND wecs.`statistics_time` >= #{beginDate}


    AND wecs.`statistics_time` <= #{endDate}

GROUP BY wecs.`statistics_time`

SELECT DATE_FORMAT(wfcr.create_time, '%Y-%m-%d') AS `time`, COUNT(wfcr.external_userid) currentNewCustomerCnt
FROM
we_flower_customer_rel wfcr
left JOIN we_user wu ON wfcr.corp_id = wu.corp_id AND wfcr.user_id = wu.user_id
WHERE
wfcr.corp_id = #{corpId}
AND wfcr.`status` NOT IN ('${@com.easyink.common.enums.CustomerStatusEnum@DRAIN.code}', '${@com.easyink.common.enums.CustomerStatusEnum@DELETE.code}')

    AND wfcr.external_userid IN

        #{externalUserId}



    AND wfcr.user_id IN

        #{userId}




    AND wu.position LIKE #{likePosition}

AND wfcr.create_time <= #{time}
GROUP BY DATE_FORMAT(wfcr.create_time, '%Y-%m-%d')

SELECT
wecs.`statistics_time` as `time`,
SUM(wecs.accumulate_customer_cnt)  AS accumulateCustomerCnt,
SUM(wecs.retain_customer_cnt) AS retainCustomerCnt
FROM
we_emple_code_statistic wecs
left JOIN we_user wu ON wecs.corp_id = wu.corp_id AND wecs.user_id = wu.user_id
WHERE wecs.corp_id = #{corpId}

    AND wecs.emple_code_id IN

        #{empleCodeId}



    AND wecs.user_id IN

        #{userId}




    AND wu.position LIKE #{likePosition}


    AND wecs.`statistics_time` >= #{beginDate}


    AND wecs.`statistics_time` <= #{endDate}

GROUP BY wecs.`statistics_time`

SELECT COUNT(wfcr.external_userid)
FROM
we_flower_customer_rel wfcr
left JOIN we_user wu ON wfcr.corp_id = wu.corp_id AND wfcr.user_id = wu.user_id
WHERE
wfcr.corp_id = #{corpId}
AND wfcr.`status` NOT IN ('${@com.easyink.common.enums.CustomerStatusEnum@DRAIN.code}', '${@com.easyink.common.enums.CustomerStatusEnum@DELETE.code}')

    AND wfcr.state IN

        #{state}



    AND wfcr.user_id IN

        #{userId}




    AND wu.position LIKE #{likePosition}

AND wfcr.create_time 
    >=
 #{beginTime}
AND wfcr.create_time 
    <=
 #{endTime}
