mapper.select:



        AND menu_name like concat('%', #{menuName}, '%')


        AND visible = #{visible}


        AND status = #{status}


order by parent_id, order_num

select distinct m.menu_id, m.parent_id, m.menu_name, m.path, m.component, m.visible, m.status, ifnull(m.perms,'') as perms, m.is_frame, m.menu_type, m.icon, m.order_num, m.create_time from sys_menu m left join sys_role_menu rm on m.menu_id = rm.menu_id left join we_user_role ur on rm.role_id = ur.role_id left join sys_role ro on ur.role_id = ro.role_id and ro.corp_id = ur.corp_id where ur.corp_id = #{params.corpId} AND ur.user_id = #{params.userId}
AND menu_name like concat('%', #{menuName}, '%')
AND visible = #{visible}
AND m.status = #{status}
order by m.parent_id, m.order_num

select count(1)
from sys_menu
where parent_id = #{menuId}

mapper.update:
update sys_menu
menu_name = #{menuName}, parent_id = #{parentId}, order_num = #{orderNum}, path = #{path}, component = #{component}, is_frame = #{isFrame}, menu_type = #{menuType}, visible = #{visible}, status = #{status}, perms = #{perms}, icon = #{icon}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate()
where menu_id = #{menuId}
