mapper.select:
SELECT
    GROUP_CONCAT(t.p_ids)
FROM
    (
        SELECT
            @ids AS p_ids,
            (
                SELECT
                    @ids := GROUP_CONCAT(id)
                FROM we_department
                WHERE corp_id = #{corpId}
                  AND FIND_IN_SET(parent_id, @ids)
            )    AS c_ids,
            @l := @l + 1 AS LEVEL
        FROM we_department,
             (SELECT @ids := #{departmentId}, @l := 0) b
        WHERE corp_id = #{corpId}
          AND @ids IS NOT NULL
    ) t

SELECT dept_id
FROM sys_role_dept
WHERE corp_id = #{corpId}
  AND role_id = #{roleId}

SELECT id
FROM we_department
WHERE corp_id = #{corpId}
