mapper.select:
select wd.id, wd.name, wd.parent_id,wd.corp_id, IFNULL ( (SELECT name FROM we_department WHERE we_department.corp_id = #{corpId} and we_department.id = wd.parent_id LIMIT 1) ,'')mainDepartmentName
from we_department wd
WHERE wd.corp_id = #{corpId}

SELECT IFNULL(group_concat(name), '')
FROM we_department wd
         LEFT JOIN we_user wu ON wu.main_department = wd.id
WHERE wd.corp_id = #{corpId}
  AND user_id = #{userId}

SELECT COUNT(0)
FROM we_user

    corp_id = #{corpId}
    AND main_department
    IN


            #{item.id}



        AND is_activate = #{isActivate}

    AND is_activate != ${@com.easywecom.common.enums.StaffActivateEnum@DELETE.code}

SELECT DISTINCT id
FROM we_department
WHERE parent_id = (SELECT min(parent_id)
                   FROM we_department
                   WHERE corp_id = #{corpId}
    LIMIT 1
    )
  and corp_id = #{corpId}

SELECT wd.id departmentId, wd.name departmentName, wd.parent_id parentId, IFNULL ( (SELECT name FROM we_department WHERE we_department.corp_id = #{corpId} AND we_department.id = wd.parent_id LIMIT 1) ,'')mainDepartmentName FROM we_department wd WHERE wd.corp_id = #{corpId}
AND wd.id IN
#{item}

mapper.update:
update we_department
name = #{name}, parent_id = #{parentId},
where id = #{id}
