mapper.select:
SELECT
wecus.id, wecus.emple_code_id,wecus.business_id,wecus.business_name,wu.mobile
FROM
we_emple_code_use_scop wecus
LEFT JOIN we_user wu ON wu.user_id = wecus.business_id
WHERE
wecus.del_flag = ${@com.easywecom.common.constant.WeConstans@WE_CUSTOMER_MSG_RESULT_NO_DEFALE}
AND wu.corp_id = #{corpId}
AND wecus.emple_code_id=#{empleCodeId}

SELECT wecus.id, wecus.emple_code_id,ifnull(wecus.business_id, '') AS business_id,party_id,wecus.business_id_type,wecus.business_name,wu.mobile FROM we_emple_code_use_scop wecus LEFT JOIN we_user wu ON wu.user_id = wecus.business_id WHERE wecus.del_flag = ${@com.easywecom.common.constant.WeConstans@WE_CUSTOMER_MSG_RESULT_NO_DEFALE} and wu.corp_id = #{corpId}
and wecus.emple_code_id in
#{empleCodeId}

SELECT
id, emple_code_id,ifnull(business_id, '') AS business_id ,party_id,business_id_type,business_name
FROM
we_emple_code_use_scop
WHERE
del_flag = ${@com.easywecom.common.constant.WeConstans@WE_CUSTOMER_MSG_RESULT_NO_DEFALE}
and business_id_type = ${@com.easywecom.common.constant.WeConstans@USE_SCOP_BUSINESSID_TYPE_ORG}

    and emple_code_id in

        #{empleCodeId}

mapper.update:
update we_emple_code_use_scop
emple_code_id = #{empleCodeId}, business_id = #{businessId}, business_name = #{businessName}, del_flag = #{delFlag},
where id = #{id}
