mapper.delete:
DELETE FROM `we_operations_center_sop`
WHERE
corp_id = #{corpId}
AND id IN

    #{item}

mapper.select:
SELECT wocs.id, wocs.corp_id, wocs.name, wocs.create_time, wocs.sop_type, wocs.filter_type, wocs.is_open, wocs.create_by, IF( wu.user_name IS NULL, wocs.create_by, wu.user_name ) createUserName, IF( wd.`name` IS NULL, '', wd.`name` ) mainDepartmentName FROM we_operations_center_sop wocs LEFT JOIN we_user wu ON wocs.corp_id = wu.corp_id AND wocs.create_by = wu.user_id LEFT JOIN we_department wd ON wocs.corp_id = wd.corp_id AND wu.main_department = wd.id LEFT JOIN we_operations_center_sop_scope wescope ON wocs.corp_id = wescope.corp_id AND wocs.id = wescope.sop_id LEFT JOIN we_user wu1 ON wescope.corp_id = wu1.corp_id AND wescope.target_id = wu1.user_id WHERE wocs.corp_id = #{corpId} AND wocs.sop_type = #{sopType}
AND wu1.user_name LIKE concat('%', #{userName}, '%')
AND wocs.is_open = #{isOpen}
AND wocs.name LIKE concat('%', #{name}, '%')
GROUP BY wocs.id ORDER BY wocs.id DESC

mapper.update:
UPDATE we_operations_center_sop
SET
  is_open = #{isOpen}
WHERE corp_id = #{corpId}
AND id IN

    #{item}

UPDATE we_operations_center_sop
SET
  `name` = #{name},
  `filter_type` = #{filterType}
WHERE corp_id = #{corpId}
AND id = #{sopId}
