mapper.select:
SELECT wt.`name`
FROM we_auto_tag_customer_scene_tag_rel cstr
         LEFT JOIN we_tag wt ON wt.tag_id = cstr.tag_id AND wt.corp_id = #{corpId}
WHERE cstr.rule_id = #{ruleId}

SELECT wt.tag_id,
       wt.name AS tag_name
FROM we_auto_tag_customer_scene_tag_rel cstr
         LEFT JOIN we_tag wt ON wt.tag_id = cstr.tag_id AND wt.corp_id = #{corpId}
WHERE cstr.customer_scene_id = #{customerSceneId}

SELECT wt.tag_id,
       wt.name AS tag_name
FROM we_auto_tag_customer_scene_tag_rel cstr
         LEFT JOIN we_tag wt ON wt.tag_id = cstr.tag_id AND wt.corp_id = #{corpId}
WHERE cstr.rule_id = #{ruleId}
GROUP BY wt.tag_id
