mapper.select:
SELECT wt.tag_id,
       wt.`name` AS tag_name
FROM we_auto_tag_keyword_tag_rel ktr
         LEFT JOIN we_tag wt ON wt.tag_id = ktr.tag_id AND wt.corp_id = #{corpId}
WHERE ktr.rule_id = #{ruleId}
GROUP BY wt.tag_id

SELECT wt.`name`
FROM we_auto_tag_keyword_tag_rel ktr
         LEFT JOIN we_tag wt ON wt.tag_id = ktr.tag_id AND wt.corp_id = #{corpId}
WHERE ktr.rule_id = #{ruleId}
