mapper.select:
SELECT wmt.id ,wmt.corp_id ,wmt.moment_id ,wmt.job_id ,wmt.content ,wmt.type ,wmt.task_type ,wmt.status ,wmt.send_time ,wmt.users ,wmt.tags FROM we_moment_task wmt LEFT JOIN we_moment_task_result wmtr ON wmtr.moment_task_id = wmt.id WHERE wmt.status = 3 AND wmt.type = 0 AND(wmtr.publish_status = 0 OR wmtr.publish_status = 3)
AND wmt.send_time < #{subDay}
AND wmt.send_time >= #{subDay}
GROUP BY wmt.id

SELECT wmt.id as momentTaskId ,wmt.corp_id ,wmt.moment_id ,wmt.job_id ,wmt.content ,wmt.type ,wmt.task_type ,wmt.status ,wmt.send_time ,wmt.users ,wmt.tags ,wmt.create_by ,wmt.create_time ,wmt.push_range ,#{content} as momentContent FROM we_moment_task wmt LEFT JOIN we_moment_detail_rel wmdr ON wmdr.moment_task_id = wmt.id LEFT JOIN we_words_detail wwd ON wmdr.detail_id = wwd.id LEFT JOIN we_moment_task_result wmtr ON wmtr.moment_task_id = wmt.id WHERE wmt.corp_id = #{corpId} AND wmt.create_by IN
#{item}
AND (wwd.content LIKE #{likeContent} OR wwd.title LIKE #{likeContent} OR wmt.content LIKE #{likeContent})
AND wmt.create_time >= #{beginTime}
AND wmt.create_time <= #{endTime}
AND wmt.type = #{type}
GROUP BY wmt.id ORDER BY wmt.create_time DESC

SELECT wu.user_name, wu.head_image_url, wmtr.publish_status, wmtr.publish_time, wmt.push_range, wu.user_id, wmtr.remark, IF(wmtr.publish_status = '${@com.easywecom.common.enums.moment.MomentPublishStatusEnum@PUBLISH.type}',GROUP_CONCAT(wc.`name`),'') customerName FROM we_moment_task wmt LEFT JOIN we_moment_task_result wmtr on wmtr.moment_task_id = wmt.id LEFT JOIN we_moment_user_customer_rel wmucr ON wmtr.moment_task_id = wmucr.moment_task_id AND wmucr.user_id = wmtr.user_id LEFT JOIN we_user wu on wu.user_id = wmtr.user_id AND wmt.corp_id = wu.corp_id LEFT JOIN we_customer wc ON wmucr.external_userid = wc.external_userid AND wc.corp_id = wmt.corp_id AND wc.type = '${@com.easywecom.common.enums.customer.CustomerTypeEnum@WX_CUSTOMER.type}' WHERE wmt.id = #{momentTaskId} AND wmt.corp_id = #{corpId}
AND wu.user_name LIKE #{likeUserName}
AND wmtr.publish_status = #{publishStatus}
GROUP BY wu.user_id
