mapper.delete:
update we_redeem_code_activity
SET del_flag = ${@com.easywecom.common.constant.WeConstans@WE_CUSTOMER_MSG_RESULT_DEFALE}
where
corp_id = #{corpId} AND id IN


        #{id}

mapper.select:
SELECT count(receive_user_id) remainInventory
FROM we_redeem_code
WHERE activity_id = #{activityId}
  and status = ${@com.easywecom.common.enums.redeemcode.RedeemCodeStatusEnum@NOT_ASSIGN.getType()}

SELECT count(receive_user_id) remainInventory
FROM we_redeem_code
WHERE activity_id = #{activityId}
  and status = ${@com.easywecom.common.enums.redeemcode.RedeemCodeStatusEnum@NOT_ASSIGN.getType()}
  AND (effective_time  >=  #{nowDate} OR
       effective_time = ${@com.easywecom.common.constant.redeemcode.RedeemCodeConstants@REDEEM_CODE_EMPTY_TIME})

SELECT count(activity_id) sumInventory
FROM we_redeem_code
WHERE activity_id = #{activityId}

SELECT DISTINCT wrca.id, wrca.name, wrca.start_time, wrca.end_time, IFNULL(wu.user_name,"admin") as create_by, wrca.create_time,
wrca.corp_id
FROM we_redeem_code_activity wrca
LEFT JOIN we_user wu ON wu.corp_id = wrca.corp_id and wrca.create_by = wu.user_id
WHERE
wrca.corp_id = #{activity.corpId} AND wrca.del_flag =
${@com.easywecom.common.constant.WeConstans@WE_CUSTOMER_MSG_RESULT_NO_DEFALE}

    AND wrca.name LIKE concat('%', #{activity.activityName}, '%')


    AND (
    wu.user_name LIKE concat('%', #{activity.createBy}, '%') OR
    wrca.create_by =  #{activity.createBy}
    )


    AND wrca.create_time >= #{activity.effectStartTime}


    AND wrca.create_time <= #{activity.effectEndTime}


    ${activity.params.dataScope}

order by create_time desc
